sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	//alert("oldValue:" + oldValue + " newValue:" + newValue);
	// NB: Important to check of unknown as the first time old value is always unknown; otherwise the condition always true
	if (isLoading || newValue === '' || (oldValue !== 'unknown' && newValue !== oldValue)) {
		return;
	}

	//Type appropriate comment here, and begin script below
	var updatedValue = newValue.toUpperCase();
	g_form.setValue('todo_description', updatedValue);

}
