sys_script_include.script:
var RahmanStringUtils = Class.create();

RahmanStringUtils.prototype={
   initialize :function(){},

   toTitleCase :function(str){
	   return str.replace(
            /\w\S*/g,
            function(txt) {
                return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            }
        );
   },

   type :'RahmanStringUtils'
};
